import { createStore } from "redux";
import { createSlice } from "@reduxjs/toolkit";
const initialState = { newYear: 0, sectorOrCountry: "" };
createSlice({
  name: "addingData",
  initialState,
  reducers: {
    addYear(state, action) {
      state.newYear = action.value;
    },
    addcountryOrSectore(state, action) {

        state.sectorOrCountry = action.value
    },
  },
});
const addData = (state = initialState, action) => {
  if (action.type === "addYear") {
    return {
      newYear: action.value,
      sectorOrCountry: state.sectorOrCountry,
    };
  }

  if (action.type === "addcountryOrSectore") {
    return {
      sectorOrCountry: action.value,
      newYear: state.newYear,
    };
  }
  return state;
};

const store = createStore(addData);

export default store;
