import {createStore} from 'redux'

const initialState = {newYear:0, countryOrSector:''}

const addYear = (state = initialState, action) => {
     if(action.type === 'addYear' ){
        return {
            newYear:action.amount,
            worldOrSector:state.countryOrSector
        }
     }

     if(action.type === 'addSectorOrCountry' ){
        return {
            newYear:action.amount,
            worldOrSector:state.countryOrSector
        }
     }
     return state
};



const store = createStore(addYear);

export default store;
