import { createStore } from "redux";

const initialState = { newYear: 2021, countryOrSector: 1 };

const addYear = (state = initialState, action) => {
  if (action.type === "addYear") {
    return {
      newYear: action.value,
      countryOrSector: state.countryOrSector,
    };
  }

  if (action.type === "addSectorOrCountry") {
    return {
      newYear: state.newYear,
      countryOrSector: action.value,
    };
  }
  return state;
};

const store = createStore(addYear);

export default store;
