import backgroundOfCircle from "../assets/img/landingPage/backgroundOfCircle.png";
import "./CircularItems.css";
function CircularItems() {
    var div = 360 / 12;
    var radius = 180;
    var offsetToParentCenter = parseInt(474 / 2); //assumes parent is square
    var offsetToChildCenter = 10;
    var totalOffset = offsetToParentCenter - offsetToChildCenter;
    function addendDiv(){
        for (var i = 1; i <= 12; ++i) {
            var childdiv = document.createElement('div');
            childdiv.className = 'div2';
            childdiv.style.position = 'absolute';
            var y = Math.sin((div * i) * (Math.PI / 180)) * radius;
            var x = Math.cos((div * i) * (Math.PI / 180)) * radius;
            childdiv.style.top = (y + totalOffset).toString() + "px";
            childdiv.style.left = (x + totalOffset).toString() + "px";
            return(<div className="div2"></div>)
          }
          

    }


  return (
    <div
      className=" col-sm-12 cirleOfElements"
      style={{
        backgroundImage: `url(${backgroundOfCircle})`,
      }}
    >
<div id="parentdiv" style={{background:'red'}}>
{
    addendDiv()
}




</div>

    </div>
  );
}
export default CircularItems;
