import { useState } from "react";
import "./LandingPage.css";
function LandingPage() {
  const [tabswitch, setTabSwitch] = useState("ქვეყნები");

  function GetTabSwitch(e) {
    const id = e.target.id;
    if (id === 's') {
      setTabSwitch("სექტორები");
    }
    if (id=== 'q') {
      setTabSwitch("ქვეყნები");
    }
  }
  console.log(tabswitch)

  return (
  
    <div className="LandingPageContainer">
      <main className="row justify-content-around align-items-center">
        <div className="col-sm-3 d-flex justify-content-center align-items-center"></div>
        <div className="col-sm-6 d-flex justify-content-center align-items-center">
          <h3
            style={{ color: "#32297C", fontSize: "1.4vw", textAlign: "center" }}
          >
            პირდაპირი უცხოური ინვესტიციები
            <br /> სექტორების მიხედვით
          </h3>
        </div>
        <div className="col-sm-2 d-flex justify-content-center align-items-center">
          <div className="wrapper">
            <div className={
              tabswitch === 'ქვეყნები' ?
              "tab-switch left text-center":
              "tab-switch right text-center"
              }>
              <div
                className={
                  tabswitch === 'ქვეყნები' ? "tab active": "tab"
                }
                tab-direction="left"
                id="q"
                onClick={(e) => GetTabSwitch(e)}
              >
                ქვეყნები
              </div>
              <div
                className={
                  tabswitch === 'სექტორები' ? "tab active": "tab"
                }
                tab-direction="right"
                id="s"
                onClick={(e) => GetTabSwitch(e)}
              >
                სექტორები
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
export default LandingPage;
